require_once("common.inc.php");



function doButtons () {
	global $USER, $ACCESS, $URL;


	if ($USER->authorize('sendphone')) {
?>
	<SoftKeyItem>
		<Name>New Job</Name>
		<URL><?= $URL . "/wiz1_job.php" ?></URL>
	<Position>1</Position>
	</SoftKeyItem>
<?
	}
	if ($USER->authorize('createreport')) {
?>
	<SoftKeyItem>
			<Name>Status</Name>
			<URL><?= $URL . "/status.php" ?></URL>
			<Position>2</Position>
	</SoftKeyItem>
<?
	}
?>
	<SoftKeyItem>
			<Name>Log out</Name>
			<URL><?= $URL . "/index.php?logout=1" ?></URL>
	<Position>3</Position>
	</SoftKeyItem>
<?
}

header("Content-type: text/xml");

if (doesSupport("CiscoIPPhoneImageFile") && !(isModel("7961") || isModel("7941"))) { ?>

	<CiscoIPPhoneImageFile>
	<LocationX>-1</LocationX>
	<LocationY>-1</LocationY>
	<URL><?= $URL . "/logo.png" ?></URL>
