	  <Prompt>Welcome to <?=$_SESSION['productname']?></Prompt>

	<? doButtons() ?>

	</CiscoIPPhoneImageFile>

<? } else if (doesSupport("CiscoIPPhoneImage")) { ?>

	<CiscoIPPhoneImage>
